mapper.insert:
insert into notice (
    user_idx, notice_title, notice_content, date
) values (
    #{userIdx}, #{noticeTitle}, #{noticeContent}, now()
)

mapper.select:
select notice_idx as noticeIdx , user_idx as userIdx , notice_title as noticeTitle , notice_content as noticeContent , date
from notice
where notice_idx = #{noticeIdx}

mapper.update:
update      notice
set         notice_title = #{noticeTitle}
          , notice_content = #{noticeContent}
where       notice_idx = #{noticeIdx}
