mapper.insert:
insert into notice (
    user_idx, notice_title, notice_content, date
) values (
    #{user_idx}, #{notice_title}, #{notice_content}, now()
)

mapper.select:
select notice_idx , user_idx , notice_title , notice_content , date
from notice
where notice_idx = #{notice_idx}

mapper.update:
update      notice
set         notice_title = #{notice_title}
          , notice_content = #{notice_content}
where       notice_idx = #{notice_idx}
