mapper.insert:
insert into payment (
    user_idx, total_price, status, date
) values (
    #{userIdx}, 0, 0, curdate()
)

mapper.select:
select      payment_idx
from        payment
where       user_idx = #{userIdx}
        and date = curdate();

select      trash_price
from        trash_dictionary
where       trash_idx = #{trashIdx};

mapper.insert:
insert into payment_detail (
        payment_idx, trash_idx, trash_weight, price
) values (
        #{paymentIdx}, #{trashIdx}, #{trashWeight}, #{price}
)

mapper.update:
update      payment
set
            total_price = total_price + #{price}
where
            payment_idx = #{paymentIdx};

mapper.select:
select     trash_history_idx   as trashHistoryIdx
         , user_idx            as userIdx
         , trash_idx           as trashIdx
         , trash_weight        as trashWeight
         , date                as date

from       trash_history
where      user_idx = #{userIdx}
       and trash_idx = #{trashIdx}
order by   date desc
limit 2;

mapper.update:
update      user
set
            exp = exp + #{exp}
where
            user_idx = #{userIdx}

mapper.select:
select      exp
from        user
where       user_idx = #{userIdx};

mapper.update:
update      user
set
            exp = exp - 10000
          , level = level + 1;
where
            user_idx = #{userIdx};

update      tree_collection
set
            count = count + 1
where
            user_idx = #{userIdx}
        and tree_idx = 1;
