mapper.select:
SELECT count(*)
  FROM trash_history
 WHERE user_idx = #{userInfo} AND trash_idx = #{type} AND date = #{date}

mapper.insert:
INSERT INTO trash_history (
    user_idx, trash_idx, trash_weight, date
) VALUES (
    #{userInfo}, #{type}, #{weight}, #{date}
)

mapper.update:
UPDATE trash_history
   SET trash_weight = trash_weight + #{weight}
 WHERE user_idx = #{userInfo} AND trash_idx = #{type} AND date = #{date}
